import React from 'react';
import ItemMessage from '../chats/ItemMessage';
import ItemMessageAttachment from '../chats/ItemMessageAttachment';
import socketIOClient from 'socket.io-client';
import { isAuthenticated } from '../controllers/UserController';
import Template from '../component/TemplateWithNavigation';

class ChatServerArea extends React.Component {
  constructor() {
    super();
    this.state = {
      messageList: [],
    }
  }

  componentDidMount() {
    try {
      document.getElementById("text-message").addEventListener("keyup", this.checkUserEnter)

      this.socket = socketIOClient(process.env.REACT_APP_API_URL, { transports: ['websocket'] });
      // wait client connect
      this.socket.on('connect', () => {
        let { chanelId } = this.props.match.params;
        let sid = this.socket.id;
        let uid = "nghia123";

        this.socket.emit("join-chanel", { sid, uid, chanelId }, () => {
          console.log("User has join this chanel", chanelId);
        })

        this.socket.on("server-send-message-from-chanel", (res) => {
          // console.log(sid, res.data.sid)
          if (sid === res.data.sid) res.data.isMe = "me";
          this.setState({ messageList: this.state.messageList.concat(res.data) })
        });
        this.socket.on("list-connected-chanel-users", users => {
          console.log(users);
        })

      });

    } catch (e) { console.log(e) }
  }

  handleSendMessageFromChanel = () => {
    try {
      window.event.preventDefault();
      let { chanelId } = this.props.match.params;
      let textMessage = document.getElementById("text-message");

      this.socket.emit("client-send-message-from-chanel", { chanelId, textMessage: textMessage.value }, () => {
        textMessage.value = "";
      })


    } catch (e) { console.log(e) }
  }

  checkUserEnter = (e) => {
    if (e.keyCode === 13) {
      try {
        window.event.preventDefault();
        let { chanelId } = this.props.match.params;
        let textMessage = document.getElementById("text-message");

        this.socket.emit("client-send-message-from-chanel", { chanelId, textMessage: textMessage.value }, () => {
          textMessage.value = "";
        })
      } catch (e) { console.log(e) }
    }
  }



  toggleDropdown = () => {
    try {
      let drdElement = document.querySelector("#chat-area .list-more");
      if (drdElement.classList.length > 2) {
        drdElement.classList.remove("active");
      } else {
        drdElement.classList.add("active");
        document.getElementById("root").addEventListener("click", () => {
          drdElement.classList.remove("active");
        });
      }
    } catch (e) { }
  }

  render() {
    let { messageList } = this.state;
    console.log(messageList)
    return (
      <Template tabPenel="d-none">
        <div id="chat-area">
          <div className="top">
            <div className="inside">
              {/* <div className="status online" />
                  <div className="data">
                    <h5><a href="#">Sarah Dalton</a></h5>
                    <span>Active now</span>
                  </div> */}
              <button className="btn d-md-block audio-call" title="Audio call">
                <i className="ti-headphone-alt" />
              </button>
              <button className="btn d-md-block audio-call" title="Audio call">
                <i className="ti-video-camera" />
              </button>
              <button className="btn d-md-block audio-call" title="Audio call">
                <i className="ti-info" />
              </button>

              <div className="dropdown" onClick={this.toggleDropdown}>
                <button className="btn d-md-block audio-call" title="Audio call">
                  <i className="ti-gird" />
                </button>

                <div className="dropdown-list list-more">
                  <a href="" className="voice">Voice Call</a>
                  <a href="" className="voice">Video Call</a>
                  <hr />
                  <a href="" className="voice">Clear History</a>
                  <a href="" className="voice">Block Contact</a>
                  <a href="" className="voice">Delete Contact</a>
                </div>
              </div>
            </div>
          </div>
          <div className="content">
            <div className="container">
              {
                messageList.map((item, i) => {
                  return (
                    <ItemMessage
                      key={i}
                      isMe={item.isMe || ""}
                      content={item.textMessage}
                      date="09:46 AM"
                    />
                  )
                })
              }


            </div>
            <div className="scroller" />
          </div>
          <div className="bottom">
            <form className="text-area">
              <textarea
                id="text-message" className="form-control" placeholder="Start typing for reply..." rows={1} defaultValue={""}

              />
              <div className="add-smiles">
                <span title="add icon" className="em em-blush" />
              </div>
              <button onClick={this.handleSendMessageFromChanel} style={{ height: '56x', outline: "none" }} type="submit" className="btn send"><i className="ti-location-arrow" /></button>
            </form>
            <label>
              <input type="file" className="d-none" />
              <span className="btn attach"><i className="ti-clip" /></span>
            </label>
          </div>
        </div>

        <div className={`container tab-panel ${display}`}>
          <div className="panel">
            <div className="setting" onClick={this.showNavigation}>
              <img className="border-radius-50" width={45} height={45} src={settingImage} alt="img-setting" />
            </div>
            <div className="logo">
              <img src={Logo} alt="brand-logo" />
            </div>
            <div className="search">
              <form action="#" name="search-friend-form" id="search-friend-form">
                <input type="text" id="search-friend" placeholder="Search for conservations ..." />
                <button className="button button-link">
                  <svg style={{ fill: '#2298ff' }} aria-hidden="true" className="svg-icon s-input-icon s-input-icon__search iconSearch" width={16} height={16} viewBox="0 0 18 18"><path d="M18 16.5l-5.14-5.18h-.35a7 7 0 1 0-1.19 1.19v.35L16.5 18l1.5-1.5zM12 7A5 5 0 1 1 2 7a5 5 0 0 1 10 0z" /></svg>
                </button>
              </form>
            </div>
            <div className="list-group filter">
              <button className="btn filterMembersBtn active" data-filter="all">
                All
                        </button>
              <button className="btn filterMembersBtn" data-filter="favourites">
                Favourites
                        </button>
              <button className="btn filterMembersBtn" data-filter="unread">
                Unread
                        </button>

            </div>

            {/* DISCUSSION */}
            <Discussion />

          </div>
          </div>
          </Template>
        )
    }
}

export default ChatServerArea;
