import { searchYoutube } from '../controllers/ChanelController';


class YoutubePlay {
    constructor(player) {
        this.onPlayQueue = [];
        this.onPlaying = false
        this.player = player;
    }

    searchYoutube(q) {
        searchYoutube(q)
            .then(res => {
                var vid = res.videoId;
                if (vid) {
                    this.onPlayQueue.push(vid);
                }

                if (!this.onPlaying) {
                    this.player.loadVideoById(this.onPlayQueue.shift());
                    this.player.playVideo();
                    this.onPlaying = true;
                }


                this.player.on('stateChange', (event) => {
                    if (event.data === 0) {
                        this.onPlaying = false;
                        if (this.onPlayQueue.length > 0) {
                            this.onPlaying = true;
                            this.player.loadVideoById(this.onPlayQueue.shift());
                            this.player.playVideo();
                        }
                    }
                });
                console.log(this.player)
                setTimeout(() => {
                    this.player.getCurrentTime().then(res => console.log(res))
                }, 3000)
            })

    }
}

export default YoutubePlay;
