import React from 'react';
import "./App.css";
import settingImage from './imgs/867443.jpg';
import Logo from './imgs/logo.png';

import Discussion from './component/Discussion';
import Navigation from './component/Navigation';
import ChatArea from './chats/ChatArea';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      showNavigation: ""
    }
  }

  showNavigation = () => {
    // this.setState()

    let nav = document.querySelector("#wrap-left .navigation") || {};
    if(nav.classList.length > 1) {
      document.getElementById("wrap-right").style.width = "calc(100% - 370px)";
      nav.classList.remove("active");
    } else {
      nav.classList.add("active");
      document.getElementById("wrap-right").style.width = "calc(100% - 370px - 95px)";
    }
  }

  componentDidMount() {
    console.log(process.env.REACT_APP_API_URL)
  }

  render() {
    let { showNavigation } = this.state;
    return (
      <>
        <div id="wrap-left">
          <Navigation active={showNavigation} />

        </div>
        <div id="wrap-right">
          <div className="container tab-content" id="nav-tabContent">
            <ChatArea />
          </div>
        </div>
      </>
    )
  }
}

export default App;
