import React from 'react';
import { Link } from 'react-router-dom';
import { getSingleChanel } from '../controllers/ChanelController';


class Chanels extends React.Component {
    constructor() {
        super();
        this.state = {
            chanels: [],
        }
    }

    componentDidMount() {
        try {
            let { chanelId } = this.props.match.params;
            // Get chanel info
            getSingleChanel(chanelId)
            .then(res => {
                console.log(res)
                if (!res.message) {
                    this.setState({
                        chanles: res
                    });
                }
            });
        } catch (e) { console.log(e) }
    }

    render() {
        let { chanels } = this.state;
        return (
            <div className="discussions" id="scroller">
                <h1>Chats</h1>
                <div className="btn-group add-group">
                    <button className="btn btn-secondary dropdown-toggle">
                        Add +
                  </button>
                </div>
                <div className="list-group chats" id="chats">
                    {
                        chanels.map((cn, index) => {
                            return <Link id={`dcs_${cn._id}`} tabIndex={index} key={index} to={`/chanels/${cn._id}`} className="item-discussions single unread">
                                <img className="avatar-md" src={cn.chanlePhoto.photoIcon} alt="chanel-icon" />
                                <div className="status online" />
                                <div className="data">
                                    <h5>{cn.chanelName}</h5>
                                    {/* <div className="new bg-red">
                                        <span>9+</span>
                                    </div> */}
                                    <span>Sun</span>
                                    <p>{}</p>
                                </div>
                            </Link>
                        })
                    }
                    <a href="#list-chat" className="item-discussions single unread">
                        <img className="avatar-md" src={settingImage} alt="avt" />
                        <div className="status online" />
                        <div className="data">
                            <h5>Bob Frank</h5>
                            <div className="new bg-red">
                                <span>9+</span>
                            </div>
                            <span>Sun</span>
                            <p>How can i improve my chances?</p>
                        </div>
                    </a>
                    <div className="scroller" />
                </div>
            </div>
        )
    }
}

export default Chanels;
