import { searchYoutube } from '../controllers/ChanelController';


class YoutubePlay {
    constructor(player) {
        this.onPlayQueue = [];
        this.onPlaying = false
        this.player = player;
    }

    searchYoutube(q) {
        searchYoutube(q)
            .then(res => {
                let vid = res.videoId;
                if (vid) {
                    this.onPlayQueue.push(vid);
                }

                if (!this.onPlaying) {
                    this.player.loadVideoById({
                        videoId: this.onPlayQueue.shift(),
                        startSeconds: 30
                    });
                    this.player.playVideo();
                    this.onPlaying = true;
                }


                this.player.on('stateChange', (event) => {
                    if (event.data === 0) {
                        if (this.onPlayQueue.length > 0) {
                            this.onPlaying = true;
                            this.player.loadVideoById(this.onPlayQueue.shift(), );
                            this.player.playVideo();
                        } else {
                            this.onPlaying = false;
                        }
                    }
                });
            })

    }

    getPlayQueue() {
        return this.onPlayQueue;
    }

    getIsPlaying() {
        return this.onPlaying;
    }

    getCurrentTime() {
        return this.player.getCurrentTime();
    }
}

export default YoutubePlay;
