    constructor() {
        super();
        this.state = {
            messageList: [],
        }
    }

    componentDidMount() {
        try {
            document.getElementById("text-message").addEventListener("keyup", this.checkUserEnter)

            this.socket = socketIOClient(process.env.REACT_APP_API_URL, {transports: ['websocket']});
